    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);
    this.events = [{
      id: 1,
      title: 'Team Standup',
      date: today.toISOString().split('T')[0],
      start_time: '09:00',
      end_time: '09:30',
      category: 'meeting',
      priority: 'medium'
    }, {
      id: 2,
      title: 'Project Planning',
      date: today.toISOString().split('T')[0],
      start_time: '14:00',
      end_time: '15:30',
      category: 'meeting',
      priority: 'high'
    }, {
      id: 3,
      title: 'Client Call',
      date: tomorrow.toISOString().split('T')[0],
      start_time: '10:00',
      end_time: '11:00',
      category: 'meeting',
      priority: 'high'
    }, {
      id: 4,
